export class BooleanHelper {
  static notNull(value) {
    return value !== null;
  }

  static notUndefined(value) {
    return value !== undefined;
  }

  static notEmptyText(value) {
    return value !== "";
  }

  static notInRange(value, min, max) {
    return !BooleanHelper.inRange(value, min, max);
  }

  static inRange(value, min, max) {
    return BooleanHelper.hasValue(value) && value >= min && value <= max;
  }

  static hasNoValue(value) {
    return !BooleanHelper.hasValue(value);
  }

  static hasValue(value) {
    return BooleanHelper.notNull(value) && BooleanHelper.notUndefined(value) && BooleanHelper.notEmptyText(value);
  }

}
